	/**
	 * 支持计算多种快递和多个城市计算快递费用
	 * @param unknown_type $key
	 * @param unknown_type $city
	 * @return Common_Model_Orders
	 */
	public function validateExpressFeesByKey($key,$city ,$pay_money=null){
		$fees = $this->_fees;
		if($key){
			$transferMethods = $this-> findTransferMethods($key);
			 $this->_fees = $transferMethods['freight'];
		} 
		if($key && $key=='a'){
			if($city == 1 || $city == '北京'){
				$this->_fees = 8;
			}
			if($pay_money!=null && $pay_money>=$this->_free_fees_max_pay_money){
				$this->_fees = 0;
			}
		}
		if($key && $key=='b'){
			if($city == 1 || $city == '北京'){
				$this->_fees = 18;
			}
			if($pay_money!=null && $pay_money>=$this->_free_fees_max_pay_money){
				$this->_fees = 0;
			}
		}
		return $this;
	}
	
