    protected function _updateOrderStatus($status,$id=null){
        if(is_null($id)){
            $id = $this->getId();
        }
        if(empty($id)){
            throw new Common_Model_Exception('Order id is Null');
        }
        $this->setId($id);
        $this->setIsNew(false);
        $this->setStatus($status);
        try{
            $this->save();
